"use strict";

var burger = document.querySelector(".burger"),
  nav = document.querySelector(".nav");
function openBurger() {
  burger.classList.toggle("open"), nav.classList.toggle("active"), bodySelector.classList.toggle("hidden");
}
burger.addEventListener("click", openBurger);
"use strict";

var catalogMobile = document.querySelector("button.menu__item"),
  subSelector = document.querySelector(".sub"),
  subButton = document.querySelector(".nav > .sub__head"),
  subBack = document.querySelector(".sub__list > .sub__back");
function mobileCatalogOpen() {
  catalogMobile.classList.toggle("active"), subSelector.classList.toggle("active"), bodySelector.classList.toggle("hidden");
}
catalogMobile.addEventListener("click", mobileCatalogOpen), subButton.addEventListener("click", function () {
  mobileCatalogOpen(), openBurger();
}), subBack.addEventListener("click", mobileCatalogOpen);
"use strict";

var subHidden = document.querySelector(".sub__hidden"),
  subHiddenButton = document.querySelector(".sub__hidden-open"),
  bodySelector = document.querySelector("body"),
  subHead = document.querySelector(".sub > .sub__head"),
  hiddenButton = document.querySelectorAll("button.sub__list-item"),
  prefixSelector = document.querySelectorAll(".prefix"),
  prefixBack = document.querySelectorAll(".prefix > .sub__back");
function subOpen() {
  subHidden.classList.toggle("active"), hiddenButton.forEach(function (e) {
    e.classList.remove("active");
  }), prefixSelector.forEach(function (e) {
    e.classList.remove("active");
  });
}
function hiddenOpen(e) {
  e.classList.contains("active") ? (e.classList.remove("active"), e.nextElementSibling.classList.remove("active")) : (hiddenButton.forEach(function (e) {
    e.classList.remove("active");
  }), prefixSelector.forEach(function (e) {
    e.classList.remove("active");
  }), e.classList.add("active"), e.nextElementSibling.classList.add("active"), window.innerWidth < 1e3 && subSelector.classList.remove("active"));
}
subHiddenButton.addEventListener("click", subOpen), subHead.addEventListener("click", subOpen), hiddenButton.forEach(function (e) {
  e.addEventListener("click", function () {
    hiddenOpen(e);
  });
}), prefixBack.forEach(function (e) {
  e.addEventListener("click", function () {
    e.parentElement.classList.remove("active"), subSelector.classList.add("active"), e.parentElement.previousElementSibling.classList.remove("active");
  });
});
"use strict";

var smallPhoto = document.querySelectorAll(".photo__left-slider_item"),
  bigPhoto = document.querySelector(".photo__right-slider_item:first-child"),
  photoLeftSlider = new Swiper(".photo__left-slider", {
    slidesPerView: 5,
    direction: "vertical",
    spaceBetween: 10,
    navigation: {
      prevEl: ".photo__left-nav.prev",
      nextEl: ".photo__left-nav.next"
    }
  });
function photoChanger(e) {
  bigPhoto.src = e.src;
}
smallPhoto.forEach(function (e) {
  e.addEventListener("click", function () {
    photoChanger(e);
  });
});
"use strict";

var mainSlider = new Swiper(".main__top-slider", {
  autoplay: !0,
  loop: !0,
  navigation: {
    prevEl: ".main__top-nav.prev",
    nextEl: ".main__top-nav.next"
  }
});
"use strict";

var tabsButton = document.querySelectorAll(".tabs__buttons-item"),
  tabsItem = document.querySelectorAll(".tabs__field-item");
function tabChanger(t, e) {
  tabsButton.forEach(function (t) {
    t.classList.remove("active");
  }), tabsItem.forEach(function (t) {
    t.classList.remove("active");
  }), tabsItem[e].classList.add("active"), t.classList.add("active");
}
tabsButton.forEach(function (t, e) {
  t.addEventListener("click", function () {
    tabChanger(t, e);
  });
});
"use strict";